import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

const FinanceDealDetails = () => {
  const { dealId } = useParams();
  const navigate = useNavigate();
  const [documents, setDocuments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  // Removed unused: setViewingIdx

  useEffect(() => {
    setLoading(true);
    fetch(`/api/deals/${dealId}`, { credentials: 'include' })
      .then(res => {
        if (!res.ok) throw new Error('Deal not found');
        return res.json();
      })
      .then(async data => {
        const deal = data.deal || data.data || data;
        const dealDocs = (data.deal && data.deal.documents) ? data.deal.documents : [];
        const vehicleDocs = Array.isArray(data.vehicleRecordDocuments) ? data.vehicleRecordDocuments : [];
        console.log('[FinanceDealDetails] Deal documents:', dealDocs);
        console.log('[FinanceDealDetails] Vehicle record documents:', vehicleDocs);
        // Merge, avoiding duplicates by fileName
        let allDocs = [...dealDocs];
        const existingNames = new Set(allDocs.map(d => d.fileName));
        vehicleDocs.forEach(doc => {
          if (!existingNames.has(doc.fileName)) allDocs.push(doc);
        });
        console.log('[FinanceDealDetails] Merged document list:', allDocs);
        setError(null);
        setLoading(false);
      })
      .catch(err => {
        setError(err.message);
        setLoading(false);
      });
  }, [dealId]);

  const handleDownload = async (doc) => {
    console.log('handleDownload called with doc:', doc);
    let downloadUrl;

    // For extra_doc files (uploaded documents), use the backoffice endpoint
    if (doc.type === 'extra_doc') {
      // Use documentId if available (new format), otherwise use type (old format)
      const identifier = doc.documentId || doc.type;
      downloadUrl = `/api/backoffice/deals/${dealId}/documents/${identifier}/download`;
      console.log('extra_doc download URL:', downloadUrl);

      // For extra_doc files, use fetch with credentials since they require authentication
      try {
        const response = await fetch(downloadUrl, { credentials: 'include' });
        if (response.ok) {
          const blob = await response.blob();
          const url = window.URL.createObjectURL(blob);
          const link = document.createElement('a');
          link.href = url;
          link.download = doc.fileName || doc.name;
          document.body.appendChild(link);
          link.click();
          window.URL.revokeObjectURL(url);
          document.body.removeChild(link);
        } else {
          throw new Error('Failed to download document');
        }
      } catch (error) {
        console.error('Error downloading document:', error);
        alert('Error downloading document');
      }
    } else {
      // For generated documents, use the documents endpoint with filename
      downloadUrl = `/api/documents/download/${doc.fileName || doc.name}`;
      console.log('generated document download URL:', downloadUrl);

      const link = document.createElement('a');
      link.href = downloadUrl;
      link.download = doc.fileName || doc.name;
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
    }
  };

  const handleView = (doc) => {
    console.log('handleView called with doc:', doc);
    let viewUrl;

    // For extra_doc files (uploaded documents), use the backoffice endpoint
    if (doc.type === 'extra_doc') {
      // Use documentId if available (new format), otherwise use type (old format)
      const identifier = doc.documentId || doc.type;
      viewUrl = `/api/backoffice/deals/${dealId}/documents/${identifier}/download`;
      console.log('extra_doc view URL:', viewUrl);

      // Navigate to the route without fileName for extra_doc files
      const navigateUrl = `/deals/${dealId}/documents/view`;
      console.log('Navigating to:', navigateUrl);
      navigate(navigateUrl, { 
        state: { viewUrl } 
      });
    } else {
      // For generated documents, use the documents endpoint with filename
      viewUrl = `/api/documents/download/${doc.fileName || doc.name}`;
      console.log('generated document view URL:', viewUrl);

      const navigateUrl = `/deals/${dealId}/documents/${encodeURIComponent(doc.fileName || doc.name)}/view`;
      console.log('Navigating to:', navigateUrl);
      navigate(navigateUrl, { 
        state: { viewUrl } 
      });
    }
  };

  if (loading) return <div className="p-8 text-white">Loading...</div>;
  if (error) return <div className="p-8 text-red-400">Error: {error}</div>;

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-900 via-gray-800 to-black p-8 flex flex-col items-center">
      <button className="mb-6 flex items-center text-blue-400 hover:text-blue-300 font-medium self-start" onClick={() => navigate(-1)}>
        <svg className="h-5 w-5 mr-2" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" d="M15 19l-7-7 7-7" /></svg>
        Back
      </button>
      <h1 className="text-3xl font-bold text-white mb-8 self-start">Deal Documents</h1>
      <div className="w-full max-w-6xl grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-8">
        {documents.length === 0 ? (
          <div className="col-span-full text-gray-400 text-center py-16 bg-white/5 rounded-2xl border border-white/10 shadow-xl">
            No documents found for this deal.
            <div className="text-xs mt-2">Debug: documents array length = {documents.length}</div>
          </div>
        ) : (
          documents.map((doc, idx) => {
            console.log(`[FinanceDealDetails] Rendering document ${idx}:`, doc);
            return (
              <div key={idx} className="flex flex-col justify-between bg-white/5 rounded-2xl border border-white/10 shadow-xl p-6 min-h-[220px] relative">
                <div>
                  <div className="text-lg font-semibold text-white truncate mb-2" title={doc.fileName || doc.name || 'Untitled'}>
                    {doc.fileName || doc.name || 'Untitled'}
                  </div>
                  <div className="flex items-center mb-2">
                    <span className="text-xs font-medium text-purple-300 bg-purple-500/10 px-2 py-1 rounded mr-2 uppercase tracking-wide">{doc.documentType || doc.type || 'N/A'}</span>
                    <span className={`text-xs font-semibold px-2 py-1 rounded-full ${doc.status === 'approved' ? 'bg-green-500/20 text-green-400' : doc.status === 'draft' ? 'bg-gray-500/20 text-gray-400' : doc.status === 'sent_to_finance' ? 'bg-orange-500/20 text-orange-400' : 'bg-red-500/20 text-red-400'}`}>{doc.status || (doc.approved ? 'approved' : doc.approved === false ? 'pending' : 'N/A')}</span>
                  </div>
                  <div className="text-xs text-gray-400 mb-4">Generated: {doc.generatedAt ? new Date(doc.generatedAt).toLocaleDateString() : 'N/A'}</div>
                  {doc.type === 'extra_doc' && doc.notes && (
                    <div className="text-xs text-blue-300 mb-2">Note: {doc.notes}</div>
                  )}
                </div>
                <div className="mt-auto flex flex-row gap-3">
                  <button
                    className="bg-blue-600 hover:bg-blue-700 text-white text-sm font-semibold py-2 px-4 rounded-lg transition-all duration-200 shadow-md"
                    onClick={() => handleView(doc)}
                  >
                    View PDF
                  </button>
                  <button
                    className="bg-gray-700 hover:bg-gray-800 text-white text-sm font-semibold py-2 px-4 rounded-lg transition-all duration-200 shadow-md"
                    onClick={() => handleDownload(doc)}
                  >
                    Download
                  </button>
                </div>
              </div>
            );
          })
        )}
      </div>
    </div>
  );
};

export default FinanceDealDetails;
