import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

const FinanceDealDetails = () => {
  const { dealId } = useParams();
  const navigate = useNavigate();
  const [documents, setDocuments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [viewingIdx, setViewingIdx] = useState(null);

  useEffect(() => {
    setLoading(true);
    fetch(`/api/deals/${dealId}`, { credentials: 'include' })
      .then(res => {
        if (!res.ok) throw new Error('Deal not found');
        return res.json();
      })
      .then(async data => {
        const deal = data.deal || data.data || data;
        // Try to get the vehicle record by _id first, then by recordId
        let vehicleRecord = null;
        let recordError = null;
        if (deal.vehicleRecordId) {
          // Try fetching by _id
          const resById = await fetch(`/api/documents/vehicle-records/by-id/${deal.vehicleRecordId}`, { credentials: 'include' });
          if (resById.ok) {
            const dataById = await resById.json();
            vehicleRecord = dataById.data || dataById.vehicleRecord || dataById;
          } else {
            recordError = 'Vehicle record not found by ID.';
          }
        }
        if (!vehicleRecord && deal.recordId) {
          // Try fetching by recordId
          const resByRecordId = await fetch(`/api/documents/vehicle-records/${deal.recordId}`, { credentials: 'include' });
          if (resByRecordId.ok) {
            const dataByRecordId = await resByRecordId.json();
            vehicleRecord = dataByRecordId.data || dataByRecordId.vehicleRecord || dataByRecordId;
          } else {
            recordError = 'Vehicle record not found by recordId.';
          }
        }
        if (!vehicleRecord) {
          setError(recordError || 'Vehicle record not found.');
          setDocuments([]);
          setLoading(false);
          return;
        }
        setDocuments(vehicleRecord.generatedDocuments || []);
        setLoading(false);
      })
      .catch(err => {
        setError(err.message);
        setLoading(false);
      });
  }, [dealId]);

  const handleView = (idx) => {
    setViewingIdx(idx);
  };

  const handleCloseView = () => {
    setViewingIdx(null);
  };

  const handleDownload = (fileName) => {
    const link = document.createElement('a');
    link.href = `/api/documents/download/${fileName}`;
    link.download = fileName;
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  if (loading) return <div className="p-8 text-white">Loading...</div>;
  if (error) return <div className="p-8 text-red-400">Error: {error}</div>;

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-900 via-gray-800 to-black p-8 flex flex-col items-center">
      <button className="mb-6 flex items-center text-blue-400 hover:text-blue-300 font-medium self-start" onClick={() => navigate(-1)}>
        <svg className="h-5 w-5 mr-2" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" d="M15 19l-7-7 7-7" /></svg>
        Back
      </button>
      <h1 className="text-3xl font-bold text-white mb-8 self-start">Deal Documents</h1>
      <div className="w-full max-w-6xl grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-8">
        {documents.length === 0 ? (
          <div className="col-span-full text-gray-400 text-center py-16 bg-white/5 rounded-2xl border border-white/10 shadow-xl">No documents found for this deal.</div>
        ) : (
          documents.map((doc, idx) => (
            <div key={idx} className="flex flex-col justify-between bg-white/5 rounded-2xl border border-white/10 shadow-xl p-6 min-h-[220px] relative">
              <div>
                <div className="text-lg font-semibold text-white truncate mb-2" title={doc.fileName || doc.name || 'Untitled'}>
                  {doc.fileName || doc.name || 'Untitled'}
                </div>
                <div className="flex items-center mb-2">
                  <span className="text-xs font-medium text-purple-300 bg-purple-500/10 px-2 py-1 rounded mr-2 uppercase tracking-wide">{doc.documentType || doc.type || 'N/A'}</span>
                  <span className={`text-xs font-semibold px-2 py-1 rounded-full ${doc.status === 'approved' ? 'bg-green-500/20 text-green-400' : doc.status === 'draft' ? 'bg-gray-500/20 text-gray-400' : doc.status === 'sent_to_finance' ? 'bg-orange-500/20 text-orange-400' : 'bg-red-500/20 text-red-400'}`}>{doc.status || (doc.approved ? 'approved' : doc.approved === false ? 'pending' : 'N/A')}</span>
                </div>
                <div className="text-xs text-gray-400 mb-4">Generated: {doc.generatedAt ? new Date(doc.generatedAt).toLocaleDateString() : 'N/A'}</div>
              </div>
              {viewingIdx === idx ? (
                <div className="absolute inset-0 bg-black/90 flex flex-col items-center justify-center z-20 rounded-2xl p-4">
                  <button className="absolute top-4 right-4 bg-gray-800 text-white rounded-full px-3 py-1 text-xs font-semibold hover:bg-gray-700" onClick={handleCloseView}>Close</button>
                  <iframe
                    src={`/api/documents/download/${doc.fileName || doc.name}`}
                    title="PDF Preview"
                    className="w-full h-80 rounded-lg border border-white/10 shadow-lg bg-white"
                  />
                </div>
              ) : (
                <div className="mt-auto flex flex-row gap-3">
                  <button
                    className="bg-blue-600 hover:bg-blue-700 text-white text-sm font-semibold py-2 px-4 rounded-lg transition-all duration-200 shadow-md"
                    onClick={() => handleView(idx)}
                  >
                    View PDF
                  </button>
                  <button
                    className="bg-gray-700 hover:bg-gray-800 text-white text-sm font-semibold py-2 px-4 rounded-lg transition-all duration-200 shadow-md"
                    onClick={() => handleDownload(doc.fileName || doc.name)}
                  >
                    Download
                  </button>
                </div>
              )}
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default FinanceDealDetails;
