import React, { useState, useEffect } from 'react';
import { ChevronDown, ChevronUp, Clock, CheckCircle, AlertCircle, FileText, Car, DollarSign, User, Calendar, ArrowLeft } from 'lucide-react';
import { useNavigate } from 'react-router-dom';

const DealStatusPage = () => {
  const [deals, setDeals] = useState([]);
  const [loading, setLoading] = useState(true);
  const [expandedDeal, setExpandedDeal] = useState(null);
  const [filter, setFilter] = useState('all');
  const navigate = useNavigate();

  useEffect(() => {
    fetchDeals();
  }, []);

  const fetchDeals = async () => {
    try {
      const response = await fetch('/api/deals', {
        credentials: 'include'
      });
      if (response.ok) {
        const data = await response.json();
        setDeals(data.deals || []);
      }
    } catch (error) {
      console.error('Error fetching deals:', error);
    } finally {
      setLoading(false);
    }
  };

  const getStatusIcon = (stage) => {
    switch (stage) {
      case 'completed':
        return <CheckCircle className="h-5 w-5 text-green-500" />;
      case 'documentation':
        return <FileText className="h-5 w-5 text-blue-500" />;
      case 'pending':
        return <Clock className="h-5 w-5 text-yellow-500" />;
      case 'issues':
        return <AlertCircle className="h-5 w-5 text-red-500" />;
      default:
        return <Clock className="h-5 w-5 text-gray-500" />;
    }
  };

  const getStatusColor = (stage) => {
    switch (stage) {
      case 'completed':
        return 'bg-green-500/20 text-green-400 border-green-500/30';
      case 'documentation':
        return 'bg-blue-500/20 text-blue-400 border-blue-500/30';
      case 'pending':
        return 'bg-yellow-500/20 text-yellow-400 border-yellow-500/30';
      case 'issues':
        return 'bg-red-500/20 text-red-400 border-red-500/30';
      default:
        return 'bg-gray-500/20 text-gray-400 border-gray-500/30';
    }
  };

  const getDealTypeIcon = (dealType) => {
    switch (dealType) {
      case 'wholesale-d2d':
        return <Car className="h-4 w-4" />;
      case 'retail':
        return <User className="h-4 w-4" />;
      case 'retail-pp':
        return <User className="h-4 w-4" />;
      default:
        return <DollarSign className="h-4 w-4" />;
    }
  };

  const getCompletionPercentage = (deal) => {
    const stages = ['initial-contact', 'documentation', 'finance', 'completion'];
    const currentIndex = stages.indexOf(deal.currentStage);
    return Math.round(((currentIndex + 1) / stages.length) * 100);
  };

  const getStageSteps = (deal) => {
    const stages = [
      { key: 'initial-contact', label: 'Initial Contact', icon: User },
      { key: 'documentation', label: 'Documentation', icon: FileText },
      { key: 'finance', label: 'Finance Review', icon: DollarSign },
      { key: 'completion', label: 'Completion', icon: CheckCircle }
    ];

    const currentIndex = stages.findIndex(stage => stage.key === deal.currentStage);

    return stages.map((stage, index) => {
      const Icon = stage.icon;
      const isCompleted = index < currentIndex;
      const isCurrent = index === currentIndex;

      return {
        ...stage,
        isCompleted,
        isCurrent,
        icon: Icon
      };
    });
  };

  const filteredDeals = deals.filter(deal => {
    if (filter === 'all') return true;
    return deal.currentStage === filter;
  });

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-900 text-white p-4">
        <div className="max-w-4xl mx-auto">
          <div className="animate-pulse">
            <div className="h-8 bg-gray-800 rounded mb-6"></div>
            <div className="space-y-4">
              {[1, 2, 3].map(i => (
                <div key={i} className="h-32 bg-gray-800 rounded-lg"></div>
              ))}
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-900 text-white p-4">
      <div className="max-w-4xl mx-auto">
        {/* Header */}
        <div className="flex items-center justify-between mb-6">
          <div className="flex items-center space-x-3">
            <button 
              onClick={() => navigate('/')}
              className="p-2 rounded-lg bg-white/10 hover:bg-white/20 transition-colors"
            >
              <ArrowLeft className="h-5 w-5" />
            </button>
            <h1 className="text-2xl font-bold">Deal Status</h1>
          </div>

          {/* Filter Tabs */}
          <div className="flex space-x-2">
            {[
              { key: 'all', label: 'All Deals' },
              { key: 'documentation', label: 'Documentation' },
              { key: 'finance', label: 'Finance' },
              { key: 'completion', label: 'Completed' }
            ].map(tab => (
              <button
                key={tab.key}
                onClick={() => setFilter(tab.key)}
                className={`px-3 py-1 rounded-lg text-sm transition-colors ${
                  filter === tab.key
                    ? 'bg-blue-600 text-white'
                    : 'bg-white/10 text-gray-300 hover:bg-white/20'
                }`}
              >
                {tab.label}
              </button>
            ))}
          </div>
        </div>

        {/* Deals List */}
        <div className="space-y-4">
          {filteredDeals.map((deal) => {
            const isExpanded = expandedDeal === deal._id;
            const stageSteps = getStageSteps(deal);
            const completionPercentage = getCompletionPercentage(deal);

            return (
              <div key={deal._id} className="bg-gray-800/50 rounded-xl border border-gray-700/50">
                {/* Deal Header */}
                <div 
                  className="p-4 cursor-pointer"
                  onClick={() => setExpandedDeal(isExpanded ? null : deal._id)}
                >
                  <div className="flex items-center justify-between">
                    <div className="flex items-center space-x-3">
                      <div className="p-2 rounded-lg bg-blue-500/20">
                        {getDealTypeIcon(deal.dealType)}
                      </div>
                      <div>
                        <h3 className="font-semibold text-lg">
                          {deal.vehicle} - {deal.dealId}
                        </h3>
                        <p className="text-gray-400 text-sm">
                          {deal.salesperson} â€¢ {new Date(deal.createdAt).toLocaleDateString()}
                        </p>
                      </div>
                    </div>

                    <div className="flex items-center space-x-3">
                      <div className={`px-3 py-1 rounded-full border text-sm ${getStatusColor(deal.currentStage)}`}>
                        {deal.currentStage}
                      </div>
                      {isExpanded ? (
                        <ChevronUp className="h-5 w-5 text-gray-400" />
                      ) : (
                        <ChevronDown className="h-5 w-5 text-gray-400" />
                      )}
                    </div>
                  </div>

                  {/* Progress Bar */}
                  <div className="mt-3">
                    <div className="flex items-center justify-between text-sm text-gray-400 mb-1">
                      <span>Progress</span>
                      <span>{completionPercentage}%</span>
                    </div>
                    <div className="w-full bg-gray-700 rounded-full h-2">
                      <div 
                        className="bg-blue-500 h-2 rounded-full transition-all duration-300"
                        style={{ width: `${completionPercentage}%` }}
                      ></div>
                    </div>
                  </div>
                </div>

                {/* Expanded Content */}
                {isExpanded && (
                  <div className="px-4 pb-4 border-t border-gray-700/50">
                    <div className="mt-4">
                      {/* Stage Steps */}
                      <div className="space-y-3">
                        {stageSteps.map((step, index) => {
                          const Icon = step.icon;
                          return (
                            <div key={step.key} className="flex items-center space-x-3">
                              <div className={`w-8 h-8 rounded-full flex items-center justify-center ${
                                step.isCompleted 
                                  ? 'bg-green-500 text-white' 
                                  : step.isCurrent 
                                    ? 'bg-blue-500 text-white'
                                    : 'bg-gray-600 text-gray-400'
                              }`}>
                                <Icon className="h-4 w-4" />
                              </div>
                              <div className="flex-1">
                                <p className={`font-medium ${
                                  step.isCompleted 
                                    ? 'text-green-400' 
                                    : step.isCurrent 
                                      ? 'text-blue-400'
                                      : 'text-gray-400'
                                }`}>
                                  {step.label}
                                </p>
                                {step.isCurrent && (
                                  <p className="text-sm text-gray-500">In progress...</p>
                                )}
                              </div>
                            </div>
                          );
                        })}
                      </div>

                      {/* Deal Details */}
                      <div className="mt-6 grid grid-cols-2 gap-4 text-sm">
                        <div>
                          <p className="text-gray-400">VIN</p>
                          <p className="font-medium">{deal.vin}</p>
                        </div>
                        <div>
                          <p className="text-gray-400">Purchase Price</p>
                          <p className="font-medium">${deal.purchasePrice?.toLocaleString()}</p>
                        </div>
                        <div>
                          <p className="text-gray-400">Deal Type</p>
                          <p className="font-medium capitalize">{deal.dealType?.replace('-', ' ')}</p>
                        </div>
                        <div>
                          <p className="text-gray-400">Created</p>
                          <p className="font-medium">{new Date(deal.createdAt).toLocaleDateString()}</p>
                        </div>
                      </div>

                      {/* Documents */}
                      {deal.documents && deal.documents.length > 0 && (
                        <div className="mt-6">
                          <h4 className="font-medium mb-3">Documents</h4>
                          <div className="space-y-2">
                            {deal.documents.map((doc, index) => (
                              <div key={index} className="flex items-center space-x-2 text-sm">
                                <FileText className="h-4 w-4 text-gray-400" />
                                <span className="text-gray-300">{doc.fileName}</span>
                                {doc.approved && (
                                  <CheckCircle className="h-4 w-4 text-green-500" />
                                )}
                              </div>
                            ))}
                          </div>
                        </div>
                      )}
                    </div>
                  </div>
                )}
              </div>
            );
          })}
        </div>

        {filteredDeals.length === 0 && (
          <div className="text-center py-12">
            <div className="text-gray-400 mb-4">
              <FileText className="h-12 w-12 mx-auto" />
            </div>
            <h3 className="text-lg font-medium text-gray-300 mb-2">No deals found</h3>
            <p className="text-gray-500">No deals match the current filter criteria.</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default DealStatusPage;
