import React, { useState, useEffect } from 'react';
import { ChevronDown, ChevronUp, Clock, CheckCircle, AlertCircle, FileText, Car, DollarSign, User, Calendar, ArrowLeft, Play, Lock } from 'lucide-react';
import { useNavigate } from 'react-router-dom';

const DealStatusPage = () => {
  const [deals, setDeals] = useState([]);
  const [loading, setLoading] = useState(true);
  const [expandedDeal, setExpandedDeal] = useState(null);
  const [filter, setFilter] = useState('all');
  const navigate = useNavigate();

  useEffect(() => {
    fetchDeals();
  }, []);

  const fetchDeals = async () => {
    try {
      const response = await fetch('/api/deals', {
        credentials: 'include'
      });
      if (response.ok) {
        const data = await response.json();
        setDeals(data.deals || []);
      }
    } catch (error) {
      console.error('Error fetching deals:', error);
    } finally {
      setLoading(false);
    }
  };

  const getStatusIcon = (stage) => {
    switch (stage) {
      case 'completed':
        return <CheckCircle className="h-4 w-4 text-green-400" />;
      case 'documentation':
        return <Play className="h-4 w-4 text-blue-400" />;
      case 'pending':
        return <Clock className="h-4 w-4 text-yellow-400" />;
      case 'issues':
        return <AlertCircle className="h-4 w-4 text-red-400" />;
      default:
        return <Lock className="h-4 w-4 text-gray-400" />;
    }
  };

  const getDealTypeIcon = (dealType) => {
    switch (dealType) {
      case 'wholesale-d2d':
        return <Car className="h-4 w-4" />;
      case 'retail':
        return <User className="h-4 w-4" />;
      case 'retail-pp':
        return <User className="h-4 w-4" />;
      default:
        return <DollarSign className="h-4 w-4" />;
    }
  };

  const getCompletionPercentage = (deal) => {
    const stages = ['initial-contact', 'documentation', 'finance', 'completion'];
    const currentIndex = stages.indexOf(deal.currentStage);
    return Math.round(((currentIndex + 1) / stages.length) * 100);
  };

  const getStageSteps = (deal) => {
    const stages = [
      { key: 'initial-contact', label: 'Initial Contact', subtitle: 'Deal created and initial contact made' },
      { key: 'documentation', label: 'Documentation', subtitle: 'Documents being prepared and reviewed' },
      { key: 'finance', label: 'Finance Review', subtitle: 'Financial terms and approval process' },
      { key: 'completion', label: 'Completion', subtitle: 'Deal finalized and closed' }
    ];

    const currentIndex = stages.findIndex(stage => stage.key === deal.currentStage);

    return stages.map((stage, index) => {
      const isCompleted = index < currentIndex;
      const isCurrent = index === currentIndex;
      const isLocked = index > currentIndex;

      return {
        ...stage,
        isCompleted,
        isCurrent,
        isLocked
      };
    });
  };

  const filteredDeals = deals.filter(deal => {
    if (filter === 'all') return true;
    return deal.currentStage === filter;
  });

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-900 text-white">
        <div className="animate-pulse">
          <div className="h-16 bg-gray-800"></div>
          <div className="p-4 space-y-4">
            {[1, 2, 3].map(i => (
              <div key={i} className="h-32 bg-gray-800 rounded-lg"></div>
            ))}
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-900 text-white">
      {/* Header */}
      <div className="bg-gray-800 border-b border-gray-700">
        <div className="flex items-center px-4 py-4">
          <button 
            onClick={() => navigate('/')}
            className="p-2 rounded-lg hover:bg-gray-700 transition-colors"
          >
            <ArrowLeft className="h-5 w-5" />
          </button>
          <h1 className="text-xl font-bold ml-3">Deal Status</h1>
        </div>

        {/* Filter Tabs */}
        <div className="px-4 pb-4">
          <div className="flex space-x-2">
            {[
              { key: 'all', label: 'All Deals' },
              { key: 'documentation', label: 'Documentation' },
              { key: 'finance', label: 'Finance' },
              { key: 'completion', label: 'Completed' }
            ].map(tab => (
              <button
                key={tab.key}
                onClick={() => setFilter(tab.key)}
                className={`px-3 py-2 rounded-lg text-sm font-medium transition-colors ${
                  filter === tab.key
                    ? 'bg-blue-600 text-white'
                    : 'text-gray-300 hover:bg-gray-700'
                }`}
              >
                {tab.label}
              </button>
            ))}
          </div>
        </div>
      </div>

      {/* Deals List */}
      <div className="p-4 space-y-4">
        {filteredDeals.map((deal) => {
          const isExpanded = expandedDeal === deal._id;
          const stageSteps = getStageSteps(deal);
          const completionPercentage = getCompletionPercentage(deal);

          return (
            <div key={deal._id} className="bg-gray-800 rounded-xl border border-gray-700">
              {/* Deal Header */}
              <div 
                className="p-4 cursor-pointer"
                onClick={() => setExpandedDeal(isExpanded ? null : deal._id)}
              >
                <div className="flex items-center justify-between">
                  <div className="flex items-center space-x-3">
                    <div className="w-10 h-10 rounded-full bg-blue-500/20 flex items-center justify-center">
                      {getDealTypeIcon(deal.dealType)}
                    </div>
                    <div className="flex-1">
                      <h3 className="font-semibold text-lg">
                        {deal.vehicle}
                      </h3>
                      <p className="text-gray-400 text-sm">
                        {deal.dealId} • {deal.salesperson}
                      </p>
                    </div>
                  </div>

                  <div className="flex items-center space-x-3">
                    <div className="text-right">
                      <p className="text-sm font-medium">${deal.purchasePrice?.toLocaleString()}</p>
                      <p className="text-xs text-gray-400">{new Date(deal.createdAt).toLocaleDateString()}</p>
                    </div>
                    {isExpanded ? (
                      <ChevronUp className="h-5 w-5 text-gray-400" />
                    ) : (
                      <ChevronDown className="h-5 w-5 text-gray-400" />
                    )}
                  </div>
                </div>
              </div>

              {/* Expanded Content */}
              {isExpanded && (
                <div className="px-4 pb-4 border-t border-gray-700">
                  <div className="mt-4">
                    {/* Progress Timeline */}
                    <div className="relative">
                      {/* Timeline Line */}
                      <div className="absolute left-4 top-0 bottom-0 w-0.5 bg-gray-600"></div>

                      {/* Stage Steps */}
                      <div className="space-y-4">
                        {stageSteps.map((step, index) => (
                          <div key={step.key} className="relative flex items-start space-x-4">
                            {/* Timeline Dot */}
                            <div className={`w-8 h-8 rounded-full flex items-center justify-center z-10 ${
                              step.isCompleted 
                                ? 'bg-green-500' 
                                : step.isCurrent 
                                  ? 'bg-blue-500'
                                  : 'bg-gray-600'
                            }`}>
                              {step.isCompleted ? (
                                <CheckCircle className="h-4 w-4 text-white" />
                              ) : step.isCurrent ? (
                                <Play className="h-4 w-4 text-white" />
                              ) : (
                                <Lock className="h-4 w-4 text-gray-300" />
                              )}
                            </div>

                            {/* Content */}
                            <div className="flex-1 pt-1">
                              <div className={`font-medium ${
                                step.isCompleted 
                                  ? 'text-green-400' 
                                  : step.isCurrent 
                                    ? 'text-blue-400'
                                    : 'text-gray-400'
                              }`}>
                                {step.label}
                              </div>
                              <div className="text-sm text-gray-500 mt-1">
                                {step.subtitle}
                              </div>
                              {step.isCurrent && (
                                <div className="mt-2 text-xs text-blue-400 bg-blue-500/10 px-2 py-1 rounded">
                                  In progress...
                                </div>
                              )}
                            </div>
                          </div>
                        ))}
                      </div>
                    </div>

                    {/* Deal Details Grid */}
                    <div className="mt-6 grid grid-cols-2 gap-4 text-sm">
                      <div>
                        <p className="text-gray-400 text-xs uppercase tracking-wide">VIN</p>
                        <p className="font-medium mt-1">{deal.vin}</p>
                      </div>
                      <div>
                        <p className="text-gray-400 text-xs uppercase tracking-wide">Deal Type</p>
                        <p className="font-medium mt-1 capitalize">{deal.dealType?.replace('-', ' ')}</p>
                      </div>
                      <div>
                        <p className="text-gray-400 text-xs uppercase tracking-wide">Mileage</p>
                        <p className="font-medium mt-1">{deal.mileage?.toLocaleString()} miles</p>
                      </div>
                      <div>
                        <p className="text-gray-400 text-xs uppercase tracking-wide">Color</p>
                        <p className="font-medium mt-1">{deal.exteriorColor || 'N/A'}</p>
                      </div>
                    </div>

                    {/* Documents Section */}
                    {deal.documents && deal.documents.length > 0 && (
                      <div className="mt-6">
                        <h4 className="font-medium mb-3 text-sm uppercase tracking-wide text-gray-400">Documents</h4>
                        <div className="space-y-2">
                          {deal.documents.map((doc, index) => (
                            <div key={index} className="flex items-center space-x-3 p-2 rounded-lg bg-gray-700/50">
                              <FileText className="h-4 w-4 text-gray-400" />
                              <span className="text-sm text-gray-300 flex-1">{doc.fileName}</span>
                              {doc.approved ? (
                                <CheckCircle className="h-4 w-4 text-green-500" />
                              ) : (
                                <Clock className="h-4 w-4 text-yellow-500" />
                              )}
                            </div>
                          ))}
                        </div>
                      </div>
                    )}
                  </div>
                </div>
              )}
            </div>
          );
        })}
      </div>

      {filteredDeals.length === 0 && (
        <div className="text-center py-12 px-4">
          <div className="text-gray-400 mb-4">
            <FileText className="h-12 w-12 mx-auto" />
          </div>
          <h3 className="text-lg font-medium text-gray-300 mb-2">No deals found</h3>
          <p className="text-gray-500">No deals match the current filter criteria.</p>
        </div>
      )}
    </div>
  );
};

export default DealStatusPage;
